import React, { useState, useEffect } from "react";
import styled from "styled-components";

const BookmarkMenu = () => {
  const [isModalOpen, setModalOpen] = useState(false);
  const [inputValue, setInputValue] = useState(""); // 입력값을 상태로 관리

  const handleInputChange = (e) => {
    // 입력값이 변경될 때마다 상태 업데이트
    setInputValue(e.target.value);
  };

  const openModal = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  const handleModalBackgroundClick = (e) => {
    // 모달 배경을 클릭했을 때 모달을 닫는 로직
    if (e.target.classList.contains("modal")) {
      closeModal();
    }
  };

  // fetch(외부api - 미리보기 텍스트) 이용 부분
  const [data, setData] = useState("");

  useEffect(() => {
    // 외부 API의 URL을 지정합니다.
    const apiUrl = "https://title.mihir.ch/https://github.com";

    // fetch를 사용하여 데이터를 가져옵니다.
    fetch(apiUrl)
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.text(); // 텍스트 데이터로 변환
      })
      .then((textData) => {
        setData(textData); // 데이터를 상태에 저장
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);


  return (
    <Wrapper>
      <p>전체 북마크</p>
      <div>
        <button>북마크 추가하기</button>
        <button onClick={openModal}>폴더 추가하기</button>
      </div>

      {isModalOpen && (
        <div className="modal" onClick={handleModalBackgroundClick}>
          <div className="modal-content">
            <span className="modal-close" onClick={closeModal}>&times;</span>
            <h2>북마크 추가하기</h2>
            <p>URL 링크</p>
            <input type="text"></input>
            <p>메모(북마크 제목)</p>
            <input
              type="text"
              value={inputValue} // 입력값을 표시
              onChange={handleInputChange} // 입력값이 변경될 때마다 호출
            />
            <button onClick={closeModal}>취소</button>
            <button>업로드하기</button>
          </div>
        </div>
      )}
    </Wrapper>
  );
};

export default BookmarkMenu;

const Wrapper = styled.div`
.modal {
  display: none;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.7); /* 배경 어두운 처리 */
  z-index: 2;
  display: flex;
  justify-content: center;
  align-items: center;
}

.modal-content {
  background-color: #ffffff;
  padding: 20px;
  border-radius: 5px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.3);
  position: relative;
}

.modal-close {
  position: absolute;
  top: 10px;
  right: 10px;
  cursor: pointer;
}
`;
