import React, { useState, useEffect } from "react";
import { useDispatch } from 'react-redux';
import styled from "styled-components";

const BookmarkMenu = () => {
  // 모달창 부분
  const [isModalOpen, setModalOpen] = useState(false);
  const openModal = () => {
    setModalOpen(true);
  };
  const closeModal = () => {
    setModalOpen(false);
  };
  const handleModalBackgroundClick = (e) => {
    if (e.target.classList.contains("modal")) {
      closeModal();
    }
  };

  const [inputUrl, setInputUrl] = useState(""); 
  const [inputTitle, setInputTitle] = useState("");
  

  return (
    <Wrapper>
      <p className="title">전체 북마크</p>
      <div>
        <button className="add-button" onClick={openModal}><p className="add-button-font">+ 북마크 추가하기</p></button>
        <button className="add-button"><p className="add-button-font">+ 폴더 추가하기</p></button>
      </div>

      {isModalOpen && (
        <div className="modal" onClick={handleModalBackgroundClick}>
          <div className="modal-box">
            <div className="modal-title">
              <span className="modal-close" onClick={closeModal}>&times;</span>
              <p className="modal-title">북마크 추가하기</p>
            </div>
            <hr />
            <div className="modal-content">
              <p>URL 링크</p>
              <input
                type="text"
                value={inputUrl}
                onChange={(e) => setInputUrl(e.target.value)} // input url 변경 시 상태 업데이트
              />
              <p>메모(북마크 제목)</p>
              <input
                type="text"
                value={inputTitle}
                onChange={(e) => setInputTitle(e.target.value)} // input 메모 변경 시 상태 업데이트
              />
              <button onClick={closeModal}>취소</button>
              <button >업로드하기</button>
            </div>
          </div>
        </div>
      )}
    </Wrapper>
  );
};

export default BookmarkMenu;

const Wrapper = styled.div`
  padding-left: 170px;
  padding-right: 170px;
  .title{
    color: #212529;
    font-size: 36px;
    font-family: 'Pret-Bold';
    font-weight: 700;
    line-height: 52px;
    word-wrap: break-word;
  }
  .add-button{
    width: 160px;
    margin: 10px;
    padding-right: 12px;
    padding-left: 12px;
    background-color: #ffffff; 
    border-radius: 8px;
    border: 1px #7749F8 solid;
    justify-content: center; 
    align-items: center; 
  }
  .add-button-font{
    color: #6610F2;
    font-size: 14px;
    font-family: 'Pret-Bold';
    font-weight: 700;
    align: center;
  }
  .modal {
    display: none;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.7); /* 배경 어두운 처리 */
    z-index: 3;
    display: flex;
    justify-content: center;
    align-items: center;
  }
  .modal-title{
    font-family: 'Pret-Bold';
    color: black;
    font-size: 16px;
    font-weight: 600;
  }
  .modal-box {
    background-color: #ffffff;
    border-radius: 5px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.3);
    position: relative;
  }
  .modal-title{
    padding-left: 10px;
    padding-right: 10px;
  }
  .modal-content{
    padding: 20px;
  }
  .modal-close {
    position: absolute;
    top: 10px;
    right: 10px;
    cursor: pointer;
  }
`;
