import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import { useLocation } from 'react-router-dom';
import { useQuery, gql } from '@apollo/client';
import { client } from "../../../client";

const GET_MYPAGE = gql`
  query getAllListByParentFolderName($parent_folder_name: String!, $user_id: Float!) {
    getAllListByParentFolderName(parent_folder_name: $parent_folder_name, user_id: $user_id) {
      folders {
        id
        folderName
        parentFolderName
      }
      bookmarks {
        id
        title
        url
        parentFolderName
      }
    }
  }
`;

const GET_MYPAGE_BASE = gql`
  query getMyPage($user_id: Float!) {
    getMyPage(user_id: $user_id) {
      folders {
        id
        folderName
        parentFolderName
        isShared
        createdAt
        updatedAt
      }
    }
  }
`;

const HomeBookmarkFolder = ({ items }) => {
  // const location = useLocation();
  // const query = new URLSearchParams(location.search);
  // folder = query.get('folder') || '전체 북마크';
  const navigate = useNavigate();
  const moveFolder = (folderName) => {
    navigate('?folder='+folderName);
  }
  useEffect (()=>{
    console.log(items)
  }, [])

  // const [ items, setItems ] = useState([]);

  // useEffect (()=>{
  //   if(folder === '전체 북마크'){
  //     client
  //     .query({
  //       query: GET_MYPAGE_BASE,
  //       variables: {
  //         user_id: 1
  //       },
  //       fetchPolicy: 'no-cache'
  //     })
  //     .then((res) => {
  //       setItems(res.data?.getMyPage.folders);
  //     })
  //     .catch((err) => {
  //       console.log(err);
  //     });
  //   } else{
  //     client
  //     .query({
  //       query: GET_MYPAGE,
  //       variables: {
  //         parent_folder_name: folder,
  //         user_id: 1
  //       },
  //       fetchPolicy: 'no-cache'
  //     })
  //     .then((res) => {
  //       setItems(res.data?.getAllListByParentFolderName.folders);
  //     })
  //     .catch((err) => {
  //       console.log(err);
  //     });
  //   }

  // }, [])
  return (
    <Wrapper>
      <div className="list-container">
        {items.map((item, index) => (
          <div key={index} className="list-item" onClick={()=>moveFolder(item.folderName)}>
            <div className="item-info">
              <div className="item-name">{item.folderName}</div>
              <div className="item-num">총 {item.id}개의 북마크</div>
            </div>
          </div>
        ))}
      </div>
    </Wrapper>
    );
};

export default HomeBookmarkFolder;

const Wrapper = styled.div`
padding-left: 170px;
padding-right: 170px;

.list-container {
  display: flex;
  flex-wrap: wrap;
}

.list-item {
  position: relative; 
  width: calc(33% - 16px); /* 3개의 아이템을 가로로 정렬하고 간격을 조절 */
  height: 90px; 
  margin: 8px; 
  background-color: #ffffff; 
  border-radius: 10px;
  border: 1px solid #DDDDDD;
  display: flex;
  align-items: center;
  justify-content: center;
}
.item-info {
  position: absolute; 
  left: 16px; 
  top: 50%; /* 상단 여백 조절 */
  transform: translateY(-50%); /* 수직 중앙 정렬 */
  z-index: 1; 
  padding:10px;
  font-family: 'Pret-reg';
}
.item-name{
  padding-right: 24px;
  font-size: 16px;
  font-weight: 700;  
  font-family: 'Pret-Bold';
}
.item-num{
  padding-right: 24px;
  color: #6C757D;
  font-size: 14px;
  font-family: 'Pret-reg';
  font-weight: 400;
  line-height: 20px;
}
`
