import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import { useQuery, gql } from '@apollo/client';
import { client } from "../../../client";

// const items = [
//   {
//     id: 1,
//     name: '기획, IA',
//     num: 3
//   },
//   {
//     id: 2,
//     name: '개발',
//     num: 2
//   },
//   {
//     id: 3,
//     name: '디자인',
//     num: 3
//   },
//   {
//     id: 4,
//     name: '레퍼런스',
//     num: 4
//   },
//   {
//     id: 5,
//     name: '기획, IA',
//     num: 3
//   },
//   {
//     id: 6,
//     name: '기획, IA',
//     num: 3
//   },
//   {
//     id: 7,
//     name: '기획, IA',
//     num: 8
//   }
// ];
const GET_MYPAGE = gql`
  query getAllListByParentFolderName($parent_folder_name: String!, $user_id: Float!) {
    getAllListByParentFolderName(parent_folder_name: $parent_folder_name, user_id: $user_id) {
      folders {
        id
        folderName
        parentFolderName
      }
      bookmarks {
        id
        title
        url
        parentFolderName
      }
    }
  }
`;

const HomeBookmarkFolder = ({ folder }) => {
  const navigate = useNavigate();

  const moveFolder = (folderName) => {
    navigate('?folder='+folderName);
  }

  const [ items, setItems ] = useState([]);

  useEffect (()=>{

    client
    .query({
      query: GET_MYPAGE,
      variables: {
        parent_folder_name: '북마크바',
        user_id: 4
      },
      fetchPolicy: 'no-cache'
    })
    .then((res) => {
      //console.log(res.data?.getAllListByParentFolderName)
      setItems(res.data?.getAllListByParentFolderName);
      console.log(items.folders);
    })
    .catch((err) => {
      console.log(err);
    });

  }, [])

  return (
    <Wrapper>
      <div className="list-container">
        {items.map((item, index) => (
          <div key={index} className="list-item" onClick={()=>moveFolder(item.name)}>
            <div className="item-info">
              <div className="item-name">{item.title}</div>
              <div className="item-num">총 {item.id}개의 북마크</div>
            </div>
          </div>
        ))}
      </div>
    </Wrapper>
    );
};

export default HomeBookmarkFolder;

const Wrapper = styled.div`
padding-left: 170px;
padding-right: 170px;

.list-container {
  display: flex;
  flex-wrap: wrap;
}

.list-item {
  position: relative; 
  width: calc(33% - 16px); /* 3개의 아이템을 가로로 정렬하고 간격을 조절 */
  height: 90px; 
  margin: 8px; 
  background-color: #ffffff; 
  border-radius: 10px;
  border: 1px solid #DDDDDD;
  display: flex;
  align-items: center;
  justify-content: center;
}
.item-info {
  position: absolute; 
  left: 16px; 
  top: 50%; /* 상단 여백 조절 */
  transform: translateY(-50%); /* 수직 중앙 정렬 */
  z-index: 1; 
  padding:10px;
  font-family: 'Pret-reg';
}
.item-name{
  padding-right: 24px;
  font-size: 16px;
  font-weight: 700;  
  font-family: 'Pret-Bold';
}
.item-num{
  padding-right: 24px;
  color: #6C757D;
  font-size: 14px;
  font-family: 'Pret-reg';
  font-weight: 400;
  line-height: 20px;
}
`
