import React, { useState, useEffect } from "react";
import { useLocation } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import { useQuery, gql } from '@apollo/client';
import { client } from "../../../client";
import { useParams } from 'react-router-dom';


import HomeBookmarkMenu from './HomeBookmarkMenu';
import HomeBookmarkFolder from './HomeBookmarkFolder';
import HomeBookmarkList from './HomeBookmarkList';

const GET_MYPAGE = gql`
  query getAllListByParentFolderName($parent_folder_name: String!, $user_id: Float!) {
    getAllListByParentFolderName(parent_folder_name: $parent_folder_name, user_id: $user_id) {
      folders {
        id
        folderName
        parentFolderName
      }
      bookmarks {
        id
        title
        url
        parentFolderName
      }
    }
  }
`;

const GET_MYPAGE_BASE = gql`
  query getMyPage($user_id: Float!) {
    getMyPage(user_id: $user_id) {
      folders {
        id
        folderName
        parentFolderName
      }
      bookmarks {
        id
        title
        url
        parentFolderName
      }
    }
  }
`;

const HomeBookmarkPages = () => {
  const location = useLocation();
  const query = new URLSearchParams(location.search);
  const { folder } = useParams();
  folder = query.get('folder') || '전체 북마크';

  const [ items, setItems ] = useState([]);

  useEffect (()=>{
    const fetchData = async () =>{
    if(folder === '전체 북마크'){
      client
      .query({
        query: GET_MYPAGE_BASE,
        variables: {
          user_id: 1
        },
        fetchPolicy: 'no-cache'
      })
      .then((res) => {
        console.log(res.data?.getMyPage.folders)
        setItems(res.data?.getMyPage.folders);
      })
      .catch((err) => {
        console.log(err);
      });
    } else{
      client
      .query({
        query: GET_MYPAGE,
        variables: {
          parent_folder_name: folder,
          user_id: 1
        },
        fetchPolicy: 'no-cache'
      })
      .then((res) => {
        setItems(res.data?.getAllListByParentFolderName.folders);
      })
      .catch((err) => {
        console.log(err);
      });
    }
  }

  }, [folder])

  return (
    <Wrapper>
      <Helmet>
        <title>내 북마크 - 북마크북</title>
        <meta
          name="description"
          content="북마크북 회원이 나의 북마크를 볼 수 있는 페이지"
        />
        <meta
          name="keywords"
          content="북마크북, 사용자, 열람"
        />
        <meta
          name="og:site_name"
          content="Bookmarkbook"
        />
        <meta
          name="og:title"
          content="내 북마크 - 북마크북"
        />
        <meta
          name="og:description"
          content="북마크북 회원이 나의 북마크를 볼 수 있는 페이지"
        />
        <meta
          name="og:type"
          content="website"
        />
        <meta
          name="og:url"
          content="https://bookmarkbook.swygbro.com"
        />
        <meta
          name="og:image"
          content="%PUBLIC_URL%/assets/images/ic_logo.png"
        />
        <meta
          name="twitter:title"
          content="내 북마크 - 북마크북"
        />
        <link
          rel="apple-touch-icon"
          href="%PUBLIC_URL%/assets/images/ic_logo.png"
        />
        <link
          rel="shortcut icon"
          href="%PUBLIC_URL%/assets/images/ic_logo.png"
        />
      </Helmet>

      <HomeBookmarkMenu folder={folder} />
      <HomeBookmarkFolder items={items} />
      <HomeBookmarkList folder={folder} />
    </Wrapper>
    );
};

export default HomeBookmarkPages;

const Wrapper = styled.div`

`
