import React from "react";
import styled from "styled-components";

const LoginUserInfo = () => {
    return (
        <Wrapper>
          <p className="title">개인정보 입력</p>
          <hr className="hr"/>
          <div className="login-components">
          <div className="intro-wrapper">
            <p className="login-intro">사용하실 <br />닉네임을 입력해주세요</p>
            <p className="nickname-intro">닉네임</p>
            <input type="text" className="input-nickname" maxLength="6" placeholder="2~6자의 한글만 입력 가능합니다."></input>
            <button className="button-nickname">다음</button>
            </div>
          </div>
        </Wrapper>
    );
};

export default LoginUserInfo;

const Wrapper = styled.div`
.login-components{
  max-width: 375px;
  margin: 0 auto; 
  display: flex;
  flex-direction: column; 
  justify-content: center; 
  align-items: center; 
  padding: 15px;
}
.intro-wrapper {
  display: flex;
  flex-direction: column;
  align-items: flex-start; /* 왼쪽 정렬을 위해 추가 */
}
.login-intro{
  color: #212529;
  font-size: 29px;
  font-family: Pretendard Variable;
  font-weight: 700;
  line-height: 32px;
  word-wrap: break-word;
}
.nickname-intro {
}
.input-nickname {
  background-color: #ffffff; 
  border-radius: 10px;
  border: 1px solid #DDDDDD;
  width: 311px;
  height: 24px;
  padding: 18px;
  color: #6c757d;
}
.button-nickname{
  width: 343px;
  "width: 100%; height: 100%; padding-left: 24px; padding-right: 24px; padding-top: 16px; padding-bottom: 16px; background: #7749F8; border-radius: 14px; justify-content: center; align-items: center; gap: 8px; display: inline-flex"
}
  .title {
    color: #212529;
    font-size: 32px;
    font-family: Pretendard Variable;
    font-weight: 700;
    line-height: 40px;
    word-wrap: break-word;
    padding-left: 170px;
    padding-right: 170px; 
  }
  .hr{
    border-top: dashed 1px #dddddd;
  }
`
