import React, { useState, useEffect } from "react";
import {BrowserRouter as Router, Route, Routes} from 'react-router-dom';
import styled from "styled-components";
import Cookies from "js-cookie";

import LoginGoogleInfo from "./LoginGoogle";
import LoginUserInfo from "./LoginUserInfo";
import LoginBookmark from "./LoginBookmark";
import LoginMoreInfo from "./LoginMoreInfo";


const Login = () => {
    const [loggedIn, setLoggedIn] = useState(false);

    useEffect(() => {
        const isLoggedIn = Cookies.get("loggedIn");
        if (isLoggedIn === "true") {
        setLoggedIn(true);
        }
    }, []);
    
    return (
        <Wrapper>
            {/* {loggedIn ? (
        <Home />
      ) : ( */}
        <div>
            <p className="title">로그인</p>
            <div className="progress-bar">
                <hr className="hr" />
            </div>
            <Routes>
                <Route path='/' element={<LoginGoogle />} />
                <Route path='/user' element={<LoginUserInfo />} />
                <Route path='/upload' element={<LoginBookmark />} />
                <Route path='/more' element={<LoginMoreInfo />} />
            </Routes>
            </div>
        {/* )} */}
        </Wrapper>
    );
};

export default Login;

const Wrapper = styled.div`
.title {
    color: #212529;
    font-size: 32px;
    font-family: Pretendard Variable;
    font-weight: 700;
    line-height: 40px;
    word-wrap: break-word;
    padding-left: 170px;
    padding-right: 170px; 
  }
  .progress-bar {
    width: 100%; 
    background: #ddd; 
  }
  .hr {
    text-align: left;
    margin-left: 0;
    height: 4px; 
    background: linear-gradient(to right, #7749F8 0%, #7749F8 50%);
    transition: width 0.5s; 
  }
`
