import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { useNavigate, useOutletContext } from 'react-router-dom';

const LoginUserInfo = () => {

  const navigate = useNavigate();
  const [title, setTitle] = useOutletContext();

  // 컴포넌트가 렌더링될 때 setTitle 함수 호출
  useEffect(() => {
    setTitle("개인정보 입력"); // 원하는 제목으로 업데이트
  }, []); // 빈 배열을 두어 한 번만 호출


  const onNextPage = () => {
    navigate("/login/upload");
  };

    return (
      <Wrapper>
      <div className="intro-wrapper">
        <p className="login-intro">사용하실 <br />닉네임을 입력해주세요</p>
        <p className="nickname-intro">닉네임</p>
          <input
            type="text"
            className="input-nickname"
            maxLength="6"
            placeholder="2~6자의 한글만 입력 가능합니다."
          ></input>
          <button className="button-nickname" onClick={onNextPage}>
             다음 
          </button>
        </div>
    </Wrapper>
    );
};

export default LoginUserInfo;

const Wrapper = styled.div`

  max-width: 375px;
  margin: 0 auto; 
  display: flex;
  flex-direction: column; 
  justify-content: center; 
  align-items: center; 
  padding: 15px;

.intro-wrapper {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
}
.login-intro{
  color: #212529;
  font-size: 24px;
  font-family: Pretendard Variable;
  font-weight: 700;
  line-height: 32px;
  word-wrap: break-word;
  width: 100%; 
  height: 100%; 
}
.nickname-intro {
}
.input-nickname {
  background-color: #ffffff; 
  border-radius: 10px;
  border: 1px solid #DDDDDD;
  width: 311px;
  height: 24px;
  padding: 18px;
  color: #6c757d;
}
.button-nickname{
  color: white;
  font-size: 16px;
  font-family: Pretendard Variable;
  font-weight: 700;
  background-color: #7749F8; 
  border-radius: 10px;
  border: 0px solid #DDDDDD;
  width: 100%; 
  height: 100%; 
  padding-left: 24px; 
  padding-right: 24px; 
  padding-top: 16px; 
  padding-bottom: 16px;
  line-height: 24px;
  word-wrap: break-word;
  margin-top: 40px;
}
`
