  .modal-title{
    font-family: 'Pret-Bold';
    color: black;
    font-size: 16px;
    font-weight: 600;
  }
  .modal-box {
    width: 388px;
    background-color: #ffffff;
    border-radius: 5px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.3);
    position: relative;
  }
  .modal-title{
    padding-left: 10px;
