  }
  .modal-input {
    width: 100%; 
    height: 38px; 
    border-radius: 4px; 
    border: 1px #ced4da solid; 
    justify-content: flex-start; 
    align-items: center; 
    display: inline-flex;

    color: #212529; 
    font-size: 15px; 
    font-family: Helvetica Neue; 
    font-weight: 400; 
    line-height: 24px; 
    word-wrap: break-word;
  }
  .modal-footer{
    padding-left: 20px;
    padding-right: 20px;
    padding-top: 10px;
    padding-bottom: 20px;
    float: right;
    align-items: flex-start; 
    gap: 8px; 
    display: inline-flex;
  }
  .modal-close {
    position: absolute;
    top: 10px;
    right: 10px;
    cursor: pointer;
  }
  .btn-exit{
    padding-left: 16px; 
