import styled from "styled-components";

const items = [
  {
    id: 1,
    name: '네이버',
    url: 'https://www.naver.com',
    thumbnail: 'https://search.pstatic.net/common/?src=http%3A%2F%2Fimgnews.naver.net%2Fimage%2F5097%2F2019%2F07%2F25%2F368292_267336_3425_20190725113616536.jpg&type=sc960_832'
  },
  {
    id: 2,
    name: '유튜브',
    url: 'https://www.youtube.com/',
    thumbnail: 'data:image/png;base64,'
  },
  {
    id: 3,
    name: '이미 push된 file .gitignore 적용하기',
    url: 'https://cjh5414.github.io/gitignore-update/',
    thumbnail: 'https://zuminternet.github.io/images/front/post/2020-09-11-Zum-Chrome-Extension/9-setting_0.png'
  },
  {
    id: 3,
    name: '이미 push된 file .gitignore 적용하기',
    url: 'https://cjh5414.github.io/gitignore-update/',
    thumbnail: 'https://zuminternet.github.io/images/front/post/2020-09-11-Zum-Chrome-Extension/9-setting_0.png'  },
  {
    id: 1,
    name: '네이버',
