    url: 'https://www.naver.com',
    thumbnail: 'https://search.pstatic.net/common/?src=http%3A%2F%2Fimgnews.naver.net%2Fimage%2F5097%2F2019%2F07%2F25%2F368292_267336_3425_20190725113616536.jpg&type=sc960_832'
  },
  {
    id: 2,
    name: '유튜브',
    url: 'https://www.youtube.com/',
    thumbnail: 'data:image/png;base64,'
  },
  {
    id: 3,
    name: '이미 push된 file .gitignore 적용하기',
    url: 'https://cjh5414.github.io/gitignore-update/',
    thumbnail: 'https://zuminternet.github.io/images/front/post/2020-09-11-Zum-Chrome-Extension/9-setting_0.png'  }
];

const BookmarkList = () => {

  return (
    <Wrapper>
      <div className="list-container">
      {items.map((item, index) => (
          <div key={index} className="list-item">
            <img url={item.thumbnail} alt={`Image ${index}`} />
            <div className="item-info">
              <div className="item-name">{item.name}</div>
              <div className="item-num">{}일 전 추가됨</div>
            </div>
          </div>
        ))}
      </div>
    </Wrapper>
    );
};

export default BookmarkList;

const Wrapper = styled.div`
padding-left: 170px;
padding-right: 170px;

.list-container {
  display: flex;
  flex-wrap: wrap;
}

.list-item {
  position: relative; /* 아이템 이름과 개수의 상대적인 위치 */
  width: calc(33.33% - 16px); /* 3개의 아이템을 가로로 정렬하고 간격을 조절 */
  height: 50px; 
  margin: 8px;
  background-color: #ccc;
  display: flex;
  align-items: center;
  justify-content: center;
}
.item-info {
  position: absolute; /* 드롭다운 메뉴가 열릴 때 위치를 고정 */
  left: 16px; /* 왼쪽 여백 */
  top: 50%; /* 상단 여백 */
  transform: translateY(-50%); /* 수직 중앙 정렬 */
  z-index: 1; /* 드롭다운 메뉴보다 위에 표시 */
}
.more-button {
  position: absolute;
  height: 24px;
  right: 0;
  cursor: pointer;
}

.dropdown-container {
  position: absolute;
  top: 100%; /* 드롭다운 메뉴가 아이템 아래쪽에 표시됨 */
  right: 0;
  z-index: 2;
}
`
