import React, { useState } from "react";
import styled from "styled-components";
import FolderDropdown from "./FolderDropdown";

const items = [
  {
    id: 1,
    name: '기획, IA',
    num: 3
  },
  {
    id: 2,
    name: '개발',
    num: 2
  },
  {
    id: 3,
    name: '디자인',
    num: 3
  },
  {
    id: 4,
    name: '레퍼런스',
    num: 4
  },
  {
    id: 5,
    name: '기획, IA',
    num: 3
  },
  {
    id: 6,
    name: '기획, IA',
    num: 3
  },
  {
    id: 7,
    name: '기획, IA',
    num: 8
  }
];


const BookmarkFolder = () => {
  const [view, setView] = useState(false); 
    return (
        <Wrapper>
          <div className="list-container">
            {items.map((item, index) => (
              <div key={index} className="list-item">
                <div className="item-name">{item.name}</div>
                <div className="item-num">{item.num}</div>
                <img src="/assets/images/more_dot.png" onClick={() => setView(!view)}/>
                {view && <FolderDropdown />}
            </div>
            ))}
          </div>
        </Wrapper>
    );
};

export default BookmarkFolder;

const Wrapper = styled.div`
.list-container {
  display: flex;
  flex-wrap: wrap;
}

.list-item {
  width: calc(33.33% - 16px); /* 3개의 아이템을 가로로 정렬하고 간격을 조절 */
  height: 100px; /* 원하는 높이로 조절 */
  margin: 8px; /* 아이템 간격 조절 */
  background-color: #ccc; /* 아이템 배경색 설정 */
  display: flex;
  align-items: center;
  justify-content: center;
}
`
