import React, { useState, useEffect } from "react";
import { Outlet, useOutletContext, useLocation } from 'react-router-dom';
import styled from "styled-components";
import Cookies from "js-cookie";

const Login = () => {
    const [loggedIn, setLoggedIn] = useState(false);
    const [title, setTitle] = useState("로그인");
    const location = useLocation();

    const getTitle = (input) => {
      setTitle(input);
    }
  
    useEffect(() => {
        const isLoggedIn = Cookies.get("loggedIn");
        if (isLoggedIn === "true") {
        setLoggedIn(true);
        }
    }, []);

    const dataFromChild = location.state && location.state.data;
    
    return (
        <Wrapper>
            {/* {loggedIn ? (
        <Home />
      ) : ( */}
            <div>
            <p className="title">{dataFromChild && <p>Data from Child: {dataFromChild}</p>}</p>
            <div className="progress-bar">
                <hr className="hr" />
            </div>
            <Outlet getTitle={getTitle} />
            </div>
        {/* )} */}
        </Wrapper>
    );
};

export default Login;

const Wrapper = styled.div`
.title {
    color: #212529;
    font-size: 32px;
    font-family: Pretendard Variable;
    font-weight: 700;
    line-height: 40px;
    word-wrap: break-word;
    padding-left: 170px;
    padding-right: 170px; 
  }
  .progress-bar {
    width: 100%; 
    background: #ddd; 
  }
  .hr {
    text-align: left;
    margin-left: 0;
    height: 4px; 
    background: linear-gradient(to right, #7749F8 0%, #7749F8 50%);
    transition: width 0.5s; 
  }
`
