import React, { useState } from "react";

const UserInfo = () => {
  // 상태를 사용하여 버튼 클릭 여부를 추적합니다.
  const [isEditing, setIsEditing] = useState(false);

  // 수정하기 버튼 클릭 시 상태를 업데이트하여 컴포넌트 교체를 처리합니다.
  const handleEditClick = () => {
    setIsEditing(true);
  };

  // 수정된 내용을 저장하고 컴포넌트를 원래 상태로 복귀합니다.
  const handleSaveClick = () => {
    setIsEditing(false);
  };

  // isEditing 값에 따라 렌더링할 컴포넌트를 선택합니다.
  return (
    <div>
      {isEditing ? (
        <UserInfoMod onSave={handleSaveClick} />
      ) : (
        <div>
          <div>
            <h1>닉네임 #1</h1>
            <button onClick={handleEditClick}>수정하기</button>
          </div>
          <p>안녕하세요</p>
        </div>
      )}
    </div>
  );
};

export default UserInfo;

// 수정 모드에서 표시될 컴포넌트
const UserInfoMod = ({ onSave }) => {
  const handleSaveClick = () => {
    // 수정된 내용을 저장한 후, 부모 컴포넌트의 상태를 업데이트하여 복귀합니다.
    onSave();
  };

  return (
    <div>
      <input type="text" placeholder="수정된 닉네임" />
      <button onClick={handleSaveClick}>저장</button>
    </div>
  );
};
